const rawInput = $input.first().json;
const body = rawInput.body || rawInput;

function containsArabic(text) {
  return /[\u0600-\u06FF]/.test(text);
}

const userMessage = (body.user_message || body.message || '').trim();
const sessionId = body.session_id || 'session_' + Date.now();
const channel = body.channel || 'chat';

let language = body.language;
if (!language) language = containsArabic(userMessage) ? 'ar' : 'en';

return {
  json: {
    user_message: userMessage,
    session_id: sessionId,
    channel: channel,
    language: language,
    timestamp: new Date().toISOString()
  }
};

const input = $input.first().json;
const msg = input.user_message;
const lang = input.language;

const intents = [
  { intent: 'LEGAL_THREAT', agent: 'escalation', ar: ['محامي', 'قانون', 'شرطة', 'هنشر', 'فيسبوك'], en: ['lawyer', 'legal', 'police', 'facebook'] },
  { intent: 'COMPLAINT', agent: 'complaint', ar: ['شكوى', 'مشكلة', 'زعلان', 'مش راضي', 'استرجاع'], en: ['complaint', 'problem', 'angry', 'refund'] },
  { intent: 'HUMAN_REQUEST', agent: 'escalation', ar: ['موظف', 'خدمة عملاء', 'مدير'], en: ['human', 'customer service', 'manager'] },
  { intent: 'WARRANTY', agent: 'warranty', ar: ['ضمان', 'تصليح', 'صيانة'], en: ['warranty', 'repair', 'maintenance'] },
  { intent: 'SALES_PRICE', agent: 'sales', ar: ['سعر', 'بكام', 'كام', 'عروض', 'خصم'], en: ['price', 'cost', 'how much', 'discount'] },
  { intent: 'SALES_REC', agent: 'sales', ar: ['عايز اشتري', 'مرتبة', 'انصحني', 'افضل'], en: ['buy', 'mattress', 'recommend', 'best'] },
  { intent: 'DELIVERY', agent: 'support', ar: ['توصيل', 'شحن', 'يوصل امتى'], en: ['delivery', 'shipping', 'arrive'] },
  { intent: 'STORE_INFO', agent: 'support', ar: ['فرع', 'عنوان', 'مواعيد'], en: ['store', 'address', 'hours', 'branch'] }
];

let detected = { intent: 'GENERAL', agent: 'support', confidence: 0.5, matched: [] };

for (const def of intents) {
  const keywords = lang === 'ar' ? def.ar : def.en;
  const matches = keywords.filter(k => msg.includes(k));
  if (matches.length > 0) {
    detected = { intent: def.intent, agent: def.agent, confidence: 0.8, matched: matches };
    break;
  }
}

return { json: { ...input, ...detected } };

