    ? `أسعار المراتب:\n• ${products[0].name_ar}: ${products[0].price} جنيه\n• ${products[1].name_ar}: ${products[1].price} جنيه\n• ${products[2].name_ar}: ${products[2].price} جنيه`
    : `Mattress prices:\n• ${products[0].name_en}: ${products[0].price} EGP\n• ${products[1].name_en}: ${products[1].price} EGP\n• ${products[2].name_en}: ${products[2].price} EGP`;
} else {
  text = lang === 'ar' ? 'أهلاً! أنا هنا أساعدك تختار المرتبة. عايز تعرف الأسعار؟' : "Hello! I'm here to help you choose. Want to know prices?";
}

return { json: { ...input, response_text: text, response_type: 'text', escalated: false, next_action: 'await_response' } };

