if (i.channel === 'chat') text = text.replace(/\n/g, '<br>');

return { json: {
  response_type: i.response_type,
  content: { text },
  next_action: i.next_action,
  agent_used: i.agent,
  intent: i.intent,
  confidence_score: i.confidence,
  session_id: i.session_id,
  language: i.language,
  channel: i.channel,
  matched_keywords: i.matched
}};

